SELECT --*
    NULL ID,
    PRO.CODPROD SKU,
    INITCAP(TRIM(
        CASE
            WHEN PRO.MARCA = 'Q BELA MAN' THEN REPLACE(PRO.DESCRPROD,'Q BELA MANUELA','')
            WHEN PRO.MARCA = 'PHA BEAUTY' THEN REPLACE(PRO.DESCRPROD,'PHALLE BEAUTY','')
            WHEN PRO.MARCA = 'AIR PURE'   THEN REPLACE(PRO.DESCRPROD,'AIRPURE','')
            WHEN PRO.MARCA = 'A HICKMAN'  THEN REPLACE(PRO.DESCRPROD,'ANA HICKMANN','')
            WHEN PRO.MARCA = 'GP REMOVE'  THEN REPLACE(PRO.DESCRPROD,'CARMESIM','')
            ELSE REPLACE(PRO.DESCRPROD,PRO.MARCA,'')
        END
    )) DESCRICAO,
    PRO.CODVOL UNIDADE,
    REGEXP_REPLACE(PRO.NCM, '^(\d{4})(\d{2})(\d{2})$', '\1.\2.\3') NCM,
    PRO.ORIGPROD,
    (   -- PREÇO MAIS RECENTE DO PRODUTO NA TABELA OUTBEAUTY
        SELECT DISTINCT
            FIRST_VALUE(EXC.VLRVENDA) OVER (ORDER BY TAB.DTVIGOR DESC) 
        FROM TGFTAB TAB INNER JOIN TGFEXC EXC ON TAB.NUTAB = EXC.NUTAB
        WHERE TAB.CODTAB = 21 AND EXC.CODPROD = PRO.CODPROD
    ) PRECO,
    0 VLR_IPI_FIXO,
    NULL OBS,
    'Ativo' SITUACAO,
    0 ESTOQUE,
    (   -- PREÇO DE CUSTO MAIS RECENTE DO PRODUTO
        SELECT DISTINCT 
            FIRST_VALUE(CUS.CUSMED) OVER (ORDER BY CUS.DTATUAL DESC)
        FROM TGFCUS CUS
        WHERE CUS.CODEMP IN (1,31) AND CUS.CODPROD = PRO.CODPROD
    ) PRECO_CUSTO,
    753240684 COD_FORNECEDOR,
    NULL FORNECEDOR,
    NULL LOCALIZACAO,
    PRO.ESTMIN ESTOQUE_MINIMO,
    PRO.ESTMAX ESTOQUE_MAXIMO,
    PRO.PESOLIQ PESO_LIQUIDO,
    PRO.PESOBRUTO PESO_BRUTO,
    PRO.REFERENCIA GTIN_EAN,
    PRO.REFERENCIA GTIN_EAN_TRIB,
    NULL DESCRICAO_COMPLEMENTAR,
    REGEXP_REPLACE(PRO.CODESPECST, '^(\d{2})(\d{3})(\d{2})$', '\1.\2.\3') CEST,
    NULL COD_IPI,
    'Caixa' FORMATO_EMBALAGEM,
    PRO.LARGURA LARGURA,
    PRO.ALTURA ALTURA,
    PRO.ESPESSURA COMPRIMENTO,
    NULL DIAMETRO,
    'S' TIPO_PRODUTO,
    NULL URL_IMG1,
    NULL URL_IMG2,
    NULL URL_IMG3,
    NULL URL_IMG4,
    NULL URL_IMG5,
    NULL URL_IMG6,
    'Teste > Nova > Categoria' CATEGORIA,
    NULL SKU_PAI,
    NULL VARIACOES,
    PRO.MARCA MARCA,
    NULL GARANTIA,
    'Não' SOB_ENCOMENDA,
    NULL PRECO_PROMO,
    NULL URL_IMG_EXT1,
    NULL URL_IMG_EXT2,
    NULL URL_IMG_EXT3,
    NULL URL_IMG_EXT4,
    NULL URL_IMG_EXT5,
    NULL URL_IMG_EXT6,
    NULL LINK_VIDEO,
    NULL TITULO_SEO,
    NULL DESCRICAO_SEO,
    NULL PALAVRA_CHAVE_SEO,
    NULL SLUG,
    NULL DIAS_PREPARACAO,
    'Sim' CONTROLA_LOTE,
    NULL UNIDADE_POR_CAIXA,
    NULL URL_IMG_EXT7,
    NULL URL_IMG_EXT8,
    NULL URL_IMG_EXT9,
    NULL URL_IMG_EXT10,
    NULL MARKUP,
    'Sim' PERMITIR_INC_VENDAS,
    NULL EX_TIPI
